mapper.select:
    select id, parent_auth_id, parent_auth_ids, name, sort, href, icon, shows, create_user_id, 
    create_time, update_user_id, update_time
    from sys_auth

		 and id = #{id,jdbcType=INTEGER}
		 and parent_auth_id = #{parentAuthId,jdbcType=INTEGER}
		 and parent_auth_ids = #{parentAuthIds,jdbcType=VARCHAR}
		 and name = #{name,jdbcType=VARCHAR}
		 and sort = #{sort,jdbcType=INTEGER}
		 and href = #{href,jdbcType=VARCHAR}
		 and icon = #{icon,jdbcType=VARCHAR}
		 and shows = #{shows,jdbcType=INTEGER}
		 and create_user_id = #{createUserId,jdbcType=INTEGER}
		 and create_time BETWEEN #{startDate} and #{endDate}
		 and update_user_id = #{updateUserId,jdbcType=INTEGER}
		 and update_time = #{updateTime,jdbcType=TIMESTAMP}


	 ${orderByString}

    select id,parent_auth_id,parent_auth_ids,name,sort,href,icon,shows,create_user_id,create_time,update_user_id,update_time
 	from  sys_auth 

		 and id = #{id,jdbcType=INTEGER}
		 and parent_auth_id = #{parentAuthId,jdbcType=INTEGER}
		 and parent_auth_ids = #{parentAuthIds,jdbcType=VARCHAR}
		 and name = #{name,jdbcType=VARCHAR}
		 and sort = #{sort,jdbcType=INTEGER}
		 and href = #{href,jdbcType=VARCHAR}
		 and icon = #{icon,jdbcType=VARCHAR}
		 and shows = #{shows,jdbcType=INTEGER}
		 and create_user_id = #{createUserId,jdbcType=INTEGER}
		 and create_time BETWEEN #{startDate} and #{endDate}
		 and update_user_id = #{updateUserId,jdbcType=INTEGER}
		 and update_time = #{updateTime,jdbcType=TIMESTAMP}


	 ${orderByString}

mapper.update:
update sys_auth
parent_auth_id = #{parentAuthId,jdbcType=INTEGER}, parent_auth_ids = #{parentAuthIds,jdbcType=VARCHAR}, `name` = #{name,jdbcType=VARCHAR}, sort = #{sort,jdbcType=INTEGER}, icon = #{icon,jdbcType=VARCHAR}, shows = #{shows,jdbcType=INTEGER}, create_user_id = #{createUserId,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=INTEGER}, update_time = #{updateTime,jdbcType=TIMESTAMP}, href = #{href,jdbcType=VARCHAR}
where id=#{id}
