mapper.select:
    select id, login_name, password, no, name, email, mobile, status, create_user_id, 
    create_time, update_user_id, update_time, remarks
    from sys_user

		 and id = #{id,jdbcType=INTEGER}
		 and login_name = #{loginName,jdbcType=VARCHAR}
		 and password = #{password,jdbcType=VARCHAR}
		 and no = #{no,jdbcType=VARCHAR}
		 and name = #{name,jdbcType=VARCHAR}
		 and email = #{email,jdbcType=VARCHAR}
		 and mobile = #{mobile,jdbcType=VARCHAR}
		 and status = #{status,jdbcType=INTEGER}
		 and create_user_id = #{createUserId,jdbcType=INTEGER}
		 and create_time BETWEEN #{startDate} and #{endDate}
		 and update_user_id = #{updateUserId,jdbcType=INTEGER}
		 and update_time = #{updateTime,jdbcType=TIMESTAMP}
		 and remarks = #{remarks,jdbcType=LONGVARCHAR}


	 ${orderByString}

    select id,login_name,password,no,name,email,mobile,status,create_user_id,create_time,update_user_id,update_time,remarks
 	from  sys_user 

		 and id = #{id,jdbcType=INTEGER}
