mapper.update:
update sys_user
login_name = #{loginName,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, no = #{no,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, mobile = #{mobile,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, update_user_id = #{updateUserId,jdbcType=INTEGER}, update_time = #{updateTime,jdbcType=TIMESTAMP}, remarks = #{remarks,jdbcType=LONGVARCHAR},
where id=#{id}
