SELECT
	test_many_to_many_user.id,
	test_many_to_many_user.name,
	test_many_to_many_user.age,
	test_many_to_many_user.status,
	test_many_to_many_user.birthday,
	test_many_to_many_user.remarks,
	test_many_to_many_user.mun,
	test_many_to_many_user.create_time,
	test_many_to_many_user.update_time
FROM
	test_many_to_many_user,test_many_to_many_user_role
WHERE test_many_to_many_user.id=test_many_to_many_user_role.user_id
and test_many_to_many_user_role.role_id=#{roleId}
 and test_many_to_many_user.id = #{id,jdbcType=INTEGER}
 and test_many_to_many_user.name = #{name,jdbcType=VARCHAR}
 and test_many_to_many_user.age = #{age,jdbcType=INTEGER}
 and test_many_to_many_user.status = #{status,jdbcType=INTEGER}
 and test_many_to_many_user.birthday = #{birthday,jdbcType=DATE}
 and test_many_to_many_user.remarks = #{remarks,jdbcType=LONGVARCHAR}
 and test_many_to_many_user.mun = #{mun,jdbcType=DECIMAL}
 and test_many_to_many_user.create_time = #{createTime,jdbcType=TIMESTAMP}
 and test_many_to_many_user.create_time BETWEEN #{startDate} and #{endDate}
 and test_many_to_many_user.update_time = #{updateTime,jdbcType=TIMESTAMP}
