mapper.update:
update test_role
name = #{name,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, create_user_id = #{createUserId,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=INTEGER}, update_time = #{updateTime,jdbcType=TIMESTAMP}, dels = #{dels,jdbcType=INTEGER},
where id=#{id}

mapper.select:
		select
		id,
		name,
		status,
		create_user_id,
		create_time,
		update_user_id,
		update_time,
		dels
		from test_role

		 and id = #{id,jdbcType=INTEGER}
		 and name = #{name,jdbcType=VARCHAR}
		 and status = #{status,jdbcType=INTEGER}
		 and create_user_id = #{createUserId,jdbcType=INTEGER}
		 and create_time = #{createTime,jdbcType=TIMESTAMP}
		 and create_time BETWEEN #{startDate} and #{endDate}
		 and update_user_id = #{updateUserId,jdbcType=INTEGER}
		 and update_time = #{updateTime,jdbcType=TIMESTAMP}
		 and dels = #{dels,jdbcType=INTEGER}



              ${orderByString}


              order by id desc

