mapper.select:
    select id, user_id, bid, buser_id, status, tender_type, auto_tender_id, money, deduction_money, 
    deductionid, repayment_money, repayment_interest, paid_money, paid_interest, wait_money, 
    wait_interest, overdue_interest, addtime, addip, btitle, bapr, additional_apr, additionalid, 
