    accrual_date, repayment_type, buy_id, additional_date
    from user_borrow_tender

		 and id = #{id,jdbcType=INTEGER}
		 and user_id = #{userId,jdbcType=INTEGER}
		 and bid = #{bid,jdbcType=INTEGER}
		 and buser_id = #{buserId,jdbcType=INTEGER}
