		 and money = #{money,jdbcType=BIGINT}
		 and deduction_money = #{deductionMoney,jdbcType=INTEGER}
		 and deductionid = #{deductionid,jdbcType=INTEGER}
		 and repayment_money = #{repaymentMoney,jdbcType=BIGINT}
